import { ZodInputOrType } from './type-utils';
import { HTTPStatusCode } from './status-codes';
export type ApiRouteServerResponse<T extends Record<number, unknown>> = {
    [K in keyof T]: {
        status: K;
        body: ZodInputOrType<T[K]>;
    };
}[keyof T] | {
    status: Exclude<HTTPStatusCode, keyof T>;
    body: unknown;
};
export declare const isAppRouteResponse: (value: unknown) => value is {
    status: HTTPStatusCode;
    body?: any;
};
export declare const validateResponse: ({ responseType, response, }: {
    responseType: unknown;
    response: {
        status: number;
        body?: unknown;
    };
}) => {
    status: number;
    body?: unknown;
};
